import React from 'react';

const AccountDetails = ({ user }) => {
    
    // Ensure the user prop is passed and contains customerID
    if (!user || !user.customerID) {
        return <div>No Customer ID available</div>;
    }

    return (
        <div className="container my-4">
            <div className="card shadow">
                <div className="card-body">
                    <h2 className="text-center mb-4">Customer ID</h2>
                    <p className="text-muted text-center">{user.customer.customerID}</p>
                </div>
            </div>
        </div>
    );
};

export default AccountDetails;
