import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const dummyCredentials = {
    userId: 'W#123',
    password: 'password123'
};

export default function Login() {
    const [userId, setUserId] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [isHovered, setIsHovered] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();
        if (userId === dummyCredentials.userId && password === dummyCredentials.password) {
            navigate('/AdminDashboard');
        } else {
            setError('Invalid User ID or password');
        }
    };

    return (
        <div style={styles.container}>
            <div style={styles.card}>
                <h1 style={styles.header}>Welcome Back!</h1>
                <p style={styles.description}>Please log in to your account.</p>
                <form onSubmit={handleSubmit} style={styles.form}>
                    <div style={styles.formGroup}>
                        <label htmlFor="userId" style={styles.label}>User ID</label>
                        <input
                            type="text"
                            id="userId"
                            placeholder="Enter your User ID"
                            value={userId}
                            onChange={(e) => setUserId(e.target.value)}
                            style={styles.input}
                        />
                    </div>
                    <div style={styles.formGroup}>
                        <label htmlFor="password" style={styles.label}>Password</label>
                        <input
                            type="password"
                            id="password"
                            placeholder="Enter your password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            style={styles.input}
                        />
                    </div>
                    {error && <div style={styles.error}>{error}</div>}
                    <button
                        type="submit"
                        style={isHovered ? { ...styles.button, ...styles.buttonHover } : styles.button}
                        onMouseEnter={() => setIsHovered(true)}
                        onMouseLeave={() => setIsHovered(false)}
                    >
                        Login
                    </button>
                </form>
            </div>
        </div>
    );
}

const styles = {
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        backgroundColor: '#f0f8ff',
        fontFamily: 'Arial, sans-serif',
    },
    card: {
        width: '100%',
        maxWidth: '400px',
        padding: '40px 40px',
        border: 'none',
        borderRadius: '15px',
        background: 'linear-gradient(135deg, #a1c4fd 0%, #c2e9fb 100%)', 
        boxShadow: '0 8px 16px rgba(0, 0, 0, 0.15)',
        textAlign: 'center',
    },
    header: {
        fontSize: '2rem',
        fontWeight: '700',
        color: '#333',
        marginBottom: '10px',
    },
    description: {
        fontSize: '1.1rem',
        color: '#444',
        marginBottom: '20px',
    },
    form: {
        width: '100%',
    },
    formGroup: {
        marginBottom: '15px',
    },
    label: {
        display: 'block',
        fontSize: '1rem',
        color: '#007bff',
        marginBottom: '5px',
        textAlign: 'left',
    },
    input: {
        width: '95%',
        padding: '8px', 
        marginBottom: '10px',
        border: '1px solid #ddd',
        borderRadius: '5px',
        fontSize: '1rem',
        transition: 'border-color 0.3s ease',
    },
    button: {
        width: '100%',
        padding: '10px 20px', 
        backgroundColor: '#007bff', 
        color: 'white',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        fontSize: '1.2rem',
        fontWeight: '600',
        transition: 'background-color 0.3s ease',
    },
    buttonHover: {
        backgroundColor: '#f48c06', 
    },
    error: {
        color: 'red',
        marginBottom: '10px',
    },
};
