import React from 'react';
import PromotionDemotion from '../shared/promotionDemotion';
import CreateCustomer from '../shared/createCustomer';
import AccountDetails from '../shared/accountDetails';
import TransactionHistory from '../shared/transactionHistory';
import WithdrawDeposit from '../shared/moneyTransfer';
import SearchCustomer from '../shared/searchCustomer';

export default function AdminDashboard() {
    return (
        <div style={styles.container}>
            <div style={styles.card}>
                <AccountDetails />
            </div>
            <div style={styles.card}>
                <TransactionHistory />
            </div>
            <div style={styles.card}>
                <WithdrawDeposit />
            </div>
            <div style={styles.card}>
                <SearchCustomer />
            </div>
            <div style={styles.card}>
                <CreateCustomer />
            </div>
            <div style={styles.card}>
                <PromotionDemotion />
            </div>
        </div>
    );
}

const styles = {
    container: {
        display: 'flex',
        flexWrap: 'wrap',
        gap: '20px',
        padding: '30px',
        justifyContent: 'center',
        backgroundColor: '#f0f8ff', 
    },
    card: {
        flex: '1 1 calc(33% - 40px)',
        margin: '10px',
        padding: '20px',
        border: 'none',
        borderRadius: '10px',
        background: 'linear-gradient(135deg, #a1c4fd 0%, #c2e9fb 100%)', // Blue gradient
        boxShadow: '0 8px 16px rgba(0, 0, 0, 0.15)',
        minWidth: '300px',
        maxWidth: '400px',
        transition: 'transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out',
    },
    cardHover: {
        transform: 'scale(1.05)',
        boxShadow: '0 12px 24px rgba(0, 0, 0, 0.2)',
    },
    header: {
        fontSize: '1.5rem',
        fontWeight: '700',
        color: '#007bff', 
        textAlign: 'center',
        marginBottom: '15px',
    },
    description: {
        fontSize: '1rem',
        color: '#444',
        textAlign: 'justify',
    },
    button: {
        display: 'block',
        width: '100%',
        padding: '12px 20px',
        marginTop: '15px',
        backgroundColor: '#ffb703', 
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        textAlign: 'center',
        transition: 'background-color 0.3s ease',
    },
    buttonHover: {
        backgroundColor: '#f48c06',
    },
};
