// Blake

import React, { useState } from 'react';
import AccountDetails from './accountDetails';

export default function SearchCustomer() {
    const [searchInput, setSearchInput] = useState('');
    const [customerDetails, setCustomerDetails] = useState(null);
    const [message, setMessage] = useState('');

    const handleInputChange = (e) => setSearchInput(e.target.value);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!searchInput) {
            setMessage('Please enter a customer ID');
            return;
        }

        try {
            // Dummy data for testing
            const dummyCustomerDetails = {
                accountNumber: '123456789',
                accountType: 'Checking',
                balance: 1000.0,
                accountHolderName: 'John Doe',
                transactions: [
                    { id: 1, date: '2024-07-31', type: 'Deposit', amount: 500 },
                    { id: 2, date: '2024-07-30', type: 'Withdrawal', amount: 200 },
                ],
            };
            setCustomerDetails(dummyCustomerDetails);
            setMessage('');

            // Uncomment below to use actual API endpoint
            // const response = await fetch(`http://localhost:4000/customers/${searchInput}`);
            // if (response.ok) {
            //     const data = await response.json();
            //     setCustomerDetails(data);
            //     setMessage('');
            // } else {
            //     setCustomerDetails(null);
            //     setMessage('Customer not found');
            // }
        } catch (error) {
            setCustomerDetails(null);
            setMessage('Error searching for customer');
        }
    };

    return (
        <div style={styles.container}>
            <h2>Search Customer</h2>
            <form onSubmit={handleSubmit} style={styles.form}>
                <div style={styles.formGroup}>
                    <label htmlFor="searchInput">Customer ID:</label>
                    <input
                        type="text"
                        id="searchInput"
                        value={searchInput}
                        onChange={handleInputChange}
                        required
                        style={styles.input}
                    />
                </div>
                <button type="submit" style={styles.button}>Search</button>
            </form>
            {message && <p style={{ color: 'red' }}>{message}</p>}
            {customerDetails && <AccountDetails details={customerDetails} />}
        </div>
    );
}

const styles = {
    container: {
        padding: '20px',
    },
    form: {
        marginBottom: '15px',
    },
    formGroup: {
        marginBottom: '15px',
    },
    input: {
        width: '100%',
        padding: '10px',
        marginBottom: '10px',
        border: '1px solid #ccc',
        borderRadius: '4px',
        fontFamily: 'Arial, sans-serif',
        boxSizing: 'border-box',
    },
    button: {
        padding: '10px',
        backgroundColor: '#007bff',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        fontFamily: 'Arial, sans-serif',
        transition: 'background-color 0.3s ease',
    },
    buttonHover: {
        backgroundColor: '#0056b3',
    },
};
