import React from 'react';
import { useNavigate } from 'react-router-dom';
import AccountDetails from '../shared/accountDetails';
import TransactionHistory from '../shared/transactionHistory';
import WithdrawDeposit from '../shared/moneyTransfer';
import 'bootstrap/dist/css/bootstrap.min.css';

// This function will display the customer dashboard along with account details, transaction history, and withdraw/deposit components

export default function CustomerDashboard({ user }) {
    const navigate = useNavigate();
    const customerAccountId = user ? user.customerID : '';

    const handleLogout = () => {
        // Clear user information from localStorage or state management
        localStorage.removeItem('user');
        // Navigate back to the login page
        navigate('/');
    };

    return (
        <div className="container-fluid py-4" style={{ backgroundColor: '#f0f8ff' }}>
            <div className="d-flex justify-content-between align-items-center mb-4">
                {user && <h2>Welcome {user.firstName} {user.lastName}!</h2>}
                <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
            </div>

            <div className="row justify-content-center">
                <div className="col-lg-4 col-md-6 mb-4">
                    <div className="card custom-card shadow-sm h-100">
                        <div className="card-body">
                            <AccountDetails />
                        </div>
                    </div>
                </div>
                <div className="col-lg-4 col-md-6 mb-4">
                    <div className="card custom-card shadow-sm h-100">
                        <div className="card-body">
                            <TransactionHistory customerAccountId={customerAccountId} />
                        </div>
                    </div>
                </div>
                <div className="col-lg-4 col-md-6 mb-4">
                    <div className="card custom-card shadow-sm h-100">
                        <div className="card-body">
                            <WithdrawDeposit />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
