// Lucky

import React, { useState, useEffect } from 'react';
//import axios from 'axios';

const AccountDetails = ({ details }) => {
    const [accountDetails, setAccountDetails] = useState(details || null);

    useEffect(() => {
        if (!details) {
            // Dummy data for testing
            const dummyData = {
                accountNumber: '123456789',
                accountType: 'Checking',
                balance: 1000.0,
                accountHolderName: 'John Doe',
                transactions: [
                    { id: 1, date: '2024-07-31', type: 'Deposit', amount: 500 },
                    { id: 2, date: '2024-07-30', type: 'Withdrawal', amount: 200 },
                ],
            };
            setAccountDetails(dummyData);

            // Uncomment below to use actual API endpoint
            // axios.get('/api/account-details')
            //     .then(response => {
            //         setAccountDetails(response.data);
            //     })
            //     .catch(error => {
            //         console.error('There was an error fetching the account details!', error);
            //     });
        }
    }, [details]);

    if (!accountDetails) {
        return <div>Loading...</div>;
    }

    return (
        <div style={styles.container}>
            <h1>Account Details</h1>
            <div style={styles.details}>
                <p><strong>Account Number:</strong> {accountDetails.accountNumber}</p>
                <p><strong>Account Type:</strong> {accountDetails.accountType}</p>
                <p><strong>Balance:</strong> ${accountDetails.balance.toFixed(2)}</p>
                <p><strong>Account Holder:</strong> {accountDetails.accountHolderName}</p>
            </div>
            <h2>Recent Transactions</h2>
            <table style={styles.table}>
                <thead>
                    <tr>
                        <th style={styles.th}>Date</th>
                        <th style={styles.th}>Type</th>
                        <th style={styles.th}>Amount</th>
                    </tr>
                </thead>
                <tbody>
                    {accountDetails.transactions.map(transaction => (
                        <tr key={transaction.id}>
                            <td style={styles.td}>{transaction.date}</td>
                            <td style={styles.td}>{transaction.type}</td>
                            <td style={styles.td}>${transaction.amount.toFixed(2)}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

const styles = {
    container: {
        padding: '20px',
    },
    details: {
        marginBottom: '20px',
    },
    table: {
        width: '100%',
        borderCollapse: 'collapse',
    },
    th: {
        border: '1px solid #ccc',
        padding: '10px',
        background: '#f9f9f9',
    },
    td: {
        border: '1px solid #ccc',
        padding: '10px',
    },
};

export default AccountDetails;
