import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

// This function will show the logged-in user’s transactions first 
// and then allows searching for other accounts’ transaction histories for admins and employees
export default function TransactionHistory({ customerAccountId, role }) {
    const [accountId, setAccountId] = useState(customerAccountId || '');
    const [transactions, setTransactions] = useState([]);
    const [filteredTransactions, setFilteredTransactions] = useState([]);
    const [filters, setFilters] = useState(new Set());
    const [message, setMessage] = useState('');
    const [view, setView] = useState(role === 'customer' ? 'my' : 'customer'); // Set view based on role

    useEffect(() => {
        if (customerAccountId) {
            fetchTransactions(customerAccountId);
        }
    }, [customerAccountId]);

    const handleInputChange = (e) => setAccountId(e.target.value);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!accountId) {
            setMessage('Please enter an account ID');
            return;
        }
        fetchTransactions(accountId);
    };

    const fetchTransactions = async (id) => {
        try {
            

            // Uncomment below to use actual API endpoint
            // const response = await fetch(`http://localhost:4000/accounts/${id}/transactions`);
            // if (response.ok) {
            //     const data = await response.json();
            //     setTransactions(data);
            //     setFilteredTransactions(data);
            //     setMessage('');
            // } else {
            //     setTransactions([]);
            //     setFilteredTransactions([]);
            //     setMessage('No transactions found for this account');
            // }
        } catch (error) {
            setTransactions([]);
            setFilteredTransactions([]);
            setMessage('Error fetching transactions');
        }
    };

    const applyFilter = (type) => {
        const newFilters = new Set(filters);
        if (newFilters.has(type)) {
            newFilters.delete(type);
        } else {
            newFilters.add(type);
        }

        setFilters(newFilters);

        if (newFilters.size === 0) {
            setFilteredTransactions(transactions);
        } else {
            const filtered = transactions.filter(transaction => newFilters.has(transaction.accountType));
            setFilteredTransactions(filtered);
        }
    };

    return (
        <div className="container my-4">
            <div className="card p-4 shadow-sm">
                <h2 className="text-center mb-4">Transaction History</h2>

                {role !== 'customer' && (
                    <div className="mb-3 d-flex justify-content-center">
                        <div className="form-check form-check-inline">
                            <input
                                className="form-check-input"
                                type="radio"
                                name="historyType"
                                id="myHistory"
                                value="my"
                                checked={view === 'my'}
                                onChange={() => setView('my')}
                            />
                            <label className="form-check-label" htmlFor="myHistory">
                                My Transaction History
                            </label>
                        </div>
                        <div className="form-check form-check-inline">
                            <input
                                className="form-check-input"
                                type="radio"
                                name="historyType"
                                id="customerHistory"
                                value="customer"
                                checked={view === 'customer'}
                                onChange={() => setView('customer')}
                            />
                            <label className="form-check-label" htmlFor="customerHistory">
                                Customer Transaction History
                            </label>
                        </div>
                    </div>
                )}

                {view === 'customer' && role !== 'customer' && (
                    <form onSubmit={handleSubmit} className="mb-4">
                        <div className="d-flex justify-content-between mb-3">
                            <input
                                type="text"
                                id="accountId"
                                value={accountId}
                                onChange={handleInputChange}
                                className="form-control me-2"
                                placeholder="Enter Account ID"
                            />
                            <button type="submit" className="btn btn-success">Search</button>
                        </div>
                    </form>
                )}

                {message && <p className="text-danger">{message}</p>}

                <div className="d-flex justify-content-center mb-3">
                    <button
                        className={`btn ${filters.has('Checking') ? 'btn-primary' : 'btn-outline-primary'} mx-1`}
                        onClick={() => applyFilter('Checking')}
                    >
                        Checking
                    </button>
                    <button
                        className={`btn ${filters.has('Savings') ? 'btn-primary' : 'btn-outline-primary'} mx-1`}
                        onClick={() => applyFilter('Savings')}
                    >
                        Savings
                    </button>
                    <button
                        className={`btn ${filters.has('Investments') ? 'btn-primary' : 'btn-outline-primary'} mx-1`}
                        onClick={() => applyFilter('Investments')}
                    >
                        Investments
                    </button>
                </div>

                {filteredTransactions.length > 0 && (
                    <div>
                        <h3 className="mb-3">Transactions</h3>
                        <div className="table-responsive" style={{ maxHeight: '300px', overflowY: 'auto' }}>
                            <table className="table table-striped">
                                <thead>
                                    <tr>
                                        <th>Date</th>
                                        <th>Type</th>
                                        <th>Amount</th>
                                        <th>Account Type</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {filteredTransactions.map(transaction => (
                                        <tr key={transaction.id}>
                                            <td>{transaction.date}</td>
                                            <td>{transaction.type}</td>
                                            <td>${transaction.amount.toFixed(2)}</td>
                                            <td>{transaction.accountType}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}
