import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Account Details needs to show the most recent transactions and make sure it fetches the correct amount for each account balance.
// This function will show the account holder's name, account ID, account type, and balance for each account. Along with the most recent transactions.
const AccountDetails = ({ details }) => {
    const [accountDetails, setAccountDetails] = useState(details || null);

    useEffect(() => {
        if (!details) {
            const user = JSON.parse(localStorage.getItem('user'));
            if (user) {
                fetch('http://localhost:4000/findCustomer', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ customerID: user.customerID }),
                })
                .then(response => response.json())
                .then(data => {
                    setAccountDetails(data);
                })
                .catch(error => {
                    console.error('There was an error fetching the account details!', error);
                });
            }
        }
    }, [details]);

    if (!accountDetails) {
        return <div className="text-center">Loading...</div>;
    }

    // Limit to 3 most recent transactions
    const recentTransactions = accountDetails.transactions.slice(0, 3);

    return (
        <div className="container my-4">
            <div className="card shadow">
                <div className="card-body">
                    <h2 className="text-center mb-4">Account Details</h2>
                    <div className="mb-3">
                        <p className="text-muted mb-1"><strong>Account Holder:</strong> {accountDetails.accountHolderName}</p>
                        <p className="text-muted mb-1"><strong>Account ID:</strong> {accountDetails.accountHolderID}</p>

                        {accountDetails.accounts.map((account, index) => (
                            <div key={index} className="border rounded p-3 mb-3">
                                <h5 className="mb-2">{account.accountType} Account</h5>
                                <p className="mb-1"><strong>Balance:</strong> ${account.balance.toFixed(2)}</p>
                            </div>
                        ))}
                    </div>
                    <h4 className="text-secondary">Recent Transactions</h4>
                    <div className="table-responsive">
                        <table className="table table-striped table-bordered">
                            <thead className="thead-dark">
                                <tr>
                                    <th>Date</th>
                                    <th>Type</th>
                                    <th>Amount</th>
                                    <th>Account Type</th>
                                </tr>
                            </thead>
                            <tbody>
                                {recentTransactions.map(transaction => (
                                    <tr key={transaction.id}>
                                        <td>{transaction.date}</td>
                                        <td>{transaction.type}</td>
                                        <td>${transaction.amount.toFixed(2)}</td>
                                        <td>{transaction.accountType}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AccountDetails;
