// Zach

import React, { useState } from 'react';

export default function PromotionDemotion() {
    const [userId, setUserId] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [selectedRole, setSelectedRole] = useState('');
    const [confirmation, setConfirmation] = useState(false);
    const [confirmedName, setConfirmedName] = useState('');
    const [confirmedRole, setConfirmedRole] = useState('');

    // Dummy data for demonstration
    const userData = {
        123: { firstName: 'John', lastName: 'Doe', role: 'employee' },
        456: { firstName: 'Jane', lastName: 'Smith', role: 'administrator' }
    };

    const handleUserIdChange = (e) => {
        const id = e.target.value;
        setUserId(id);
        if (userData[id]) {
            setFirstName(userData[id].firstName);
            setLastName(userData[id].lastName);
            setSelectedRole(userData[id].role); // Set initial role based on dummy data
        } else {
            setFirstName('');
            setLastName('');
            setSelectedRole(''); // Clear role if no user found
        }
    };

    const handleRoleChange = (role) => {
        setSelectedRole(role);
    };

    const handleSubmit = () => {
        if (userData[userId] && selectedRole) {
            setConfirmedName(`${firstName} ${lastName}`);
            setConfirmedRole(selectedRole);
            setConfirmation(true);
            setTimeout(() => {
                setConfirmation(false);
            }, 3000);

            // Clear input fields after submission
            setUserId('');
            setFirstName('');
            setLastName('');
            setSelectedRole('');
        } else {
            alert('Please enter a valid User ID and select a role.');
        }
    };

    return (
        <div style={styles.container}>
            <h1>Role Management</h1>
            <div style={styles.formGroup}>
                <label htmlFor="userId">User ID</label>
                <input
                    type="text"
                    id="userId"
                    placeholder="Enter User ID"
                    value={userId}
                    onChange={handleUserIdChange}
                    style={styles.input}
                />
            </div>
            <div style={styles.formGroup}>
                <label htmlFor="firstName">First Name</label>
                <input
                    type="text"
                    id="firstName"
                    placeholder="First Name"
                    value={firstName}
                    disabled
                    style={styles.input}
                />
            </div>
            <div style={styles.formGroup}>
                <label htmlFor="lastName">Last Name</label>
                <input
                    type="text"
                    id="lastName"
                    placeholder="Last Name"
                    value={lastName}
                    disabled
                    style={styles.input}
                />
            </div>
            <div style={styles.roles}>
                <button
                    onClick={() => handleRoleChange('customer')}
                    style={{ ...styles.button, ...(selectedRole === 'customer' ? styles.selectedButton : {}) }}
                >
                    Customer
                </button>
                <button
                    onClick={() => handleRoleChange('employee')}
                    style={{ ...styles.button, ...(selectedRole === 'employee' ? styles.selectedButton : {}) }}
                >
                    Employee
                </button>
                <button
                    onClick={() => handleRoleChange('administrator')}
                    style={{ ...styles.button, ...(selectedRole === 'administrator' ? styles.selectedButton : {}) }}
                >
                    Administrator
                </button>
            </div>

            <button
                onClick={handleSubmit}
                style={styles.submitButton}
            >
                Submit Change
            </button>
            {confirmation && (
                <div className="confirmation" style={styles.confirmation}>
                    {`Role of ${confirmedName} changed to ${confirmedRole}!`}
                </div>
            )}
        </div>
    );
}

const styles = {
    container: {
        padding: '20px',
    },
    formGroup: {
        marginBottom: '15px',
    },
    input: {
        width: '100%',
        padding: '10px',
        marginBottom: '10px',
        border: '1px solid #ccc',
        borderRadius: '4px',
        fontFamily: 'Arial, sans-serif',
        boxSizing: 'border-box',
    },
    roles: {
        display: 'flex',
        justifyContent: 'space-between',
        marginBottom: '15px',
    },
    button: {
        flex: '1',
        padding: '10px',
        marginRight: '5px',
        backgroundColor: '#f0f0f0',
        border: '1px solid #ccc',
        cursor: 'pointer',
        fontFamily: 'Arial, sans-serif',
        textAlign: 'center',
        transition: 'background-color 0.3s ease',
    },
    selectedButton: {
        backgroundColor: '#007bff',
        color: 'white',
    },
    submitButton: {
        width: '100%',
        padding: '10px',
        backgroundColor: '#007bff',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        fontFamily: 'Arial, sans-serif',
        transition: 'background-color 0.3s ease',
    },
    buttonHover: {
        backgroundColor: '#0056b3',
    },
    confirmation: {
        padding: '10px',
        backgroundColor: '#d4edda',
        color: '#155724',
        marginTop: '20px',
        border: '1px solid #c3e6cb',
    },
};
